package allprograms;

public class program1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;
		int b = 1;
		int c;
		int d;
		c = ++b; 
		d = a++;
		c++;
		System.out.println("a = " + a);
		System.out.println("b = " + b);
		System.out.println("c = " + c);
		System.out.println("d = " + d);
    }
}

